    public static void main(String[] args) {
        printSimilarity("A1 A2", vecA_1, vecA_2);
        printSimilarity("A1 B1", vecA_1, vecB_1);
        printSimilarity("A1 B2", vecA_1, vecB_2);
        printSimilarity("A2 B1", vecA_2, vecB_1);
        printSimilarity("A2 B2", vecA_2, vecB_2);
        printSimilarity("B1 B2", vecB_1, vecB_2);
        printSimilarity("A1 C", vecA_1, vecC);
        printSimilarity("B1 C", vecB_1, vecC);
        printSimilarity("A2 C", vecA_2, vecC);
        printSimilarity("B2 C", vecB_2, vecC);
    }

    public static void printSimilarity(String tag, double[] vectorA, double[] vectorB) {
        System.out.println(tag + " - Cosine: " + cosineSimilarity(vectorA, vectorB)
                + " - Euclidean: " + euclideanDistance(vectorA, vectorB)
                + " - Hamming: " + hammingDistance(vectorA, vectorB));
    }

    public static double cosineSimilarity(double[] vectorA, double[] vectorB) {
        double dotProduct = 0.0;
        double normA = 0.0;
        double normB = 0.0;
        for (int i = 0; i < vectorA.length; i++) {
            dotProduct += vectorA[i] * vectorB[i];
            normA += Math.pow(vectorA[i], 2);
            normB += Math.pow(vectorB[i], 2);
        }
        return 1 - dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
    }


    public static double euclideanDistance(double[] vectorA, double[] vectorB) {
        double output = 0.0;
        for (int i = 0; i < vectorA.length; i++) {
            output += Math.pow(vectorA[i] - vectorB[i], 2);
        }
        return Math.sqrt(output);
    }

    public static double hammingDistance(double[] vectorA, double[] vectorB) {
        double output = 0.0;
        for (int i = 0; i < vectorA.length; i++) {
            output += (Math.abs(vectorA[i] - vectorB[i]) > 1) ? 1 : 0;
        }
        return (output);
    }
